import React from 'react';
import img1 from '../assets/img-1.png';
import {useForm} from 'react-hook-form';

export default function Form() {

    const {register, handleSubmit, watch, formState:{errors}} = useForm()
    const onSubmit = data =>console.log(data);

    console.log(watch('username'));


  return (
   <section>
    <div className='register'>
        <div className='col-1'>
            <h2>Sign In</h2>
            <span>register and enjoy the service</span>
            <form id='form' className='flex flex-col' onSubmit={handleSubmit(onSubmit)}>
                <input type='text'{...register("username",{required:true, maxLength:10})}placeholder='username'/>
                <input type='text' {...register("password",{required:true, maxLength:10})}placeholder='password'/>
                <input type='text' {...register("comfirmpwd",{required:true, maxLength:10})}placeholder='comfirm password'/>
                <input type='text' {...register("mobile",{required:true, maxLength:10})}placeholder='mobile number'/>
{errors.mobile?.type ==="required" && "Mobile Number is required"}
{errors.mobile?.type ==="MaxLenght" && "MaxLenght is Exceed"}
{errors.username?.type ==="required" && "Username is required"}
{errors.password?.type ==="required" && "Password is required"}
{errors.comfirmpwd?.type ==="required" && "Comfirm password is required"}

                <button className='btn'> Sign In</button>



            </form>
        </div>
        <div className='col-2'>
<img src={img1} alt=''/>

        </div>
        <div className='col-3'></div>
    </div>
   </section>
  )
}
